/* 自定义样式 - Onism's Blog - 人像摄影美学风格 */

:root {
  /* 主色调 - 硬朗男性化色系 */
  --primary-color: #0ea5e9;
  --secondary-color: #1e293b;
  --accent-color: #f59e0b;
  --dark-slate: #0f172a;
  --light-slate: #e2e8f0;
  --steel-gray: #64748b;
  --gradient-main: linear-gradient(135deg, #0ea5e9, #1e293b, #f59e0b);
  --gradient-soft: linear-gradient(45deg, #f8fafc, #e2e8f0);
  --text-elegant: #1e293b;
  --bg-glass: rgba(14, 165, 233, 0.06);
}

/* 全局动画效果 - 优化性能 */
a, button, .btn, .recent-post-item, .card-widget {
  transition: transform 0.3s ease, opacity 0.3s ease, box-shadow 0.3s ease;
}

/* 页面加载动画 - 简化版本 */
body {
  animation: simpleFadeIn 0.6s ease-out;
  background: var(--gradient-soft);
}

@keyframes simpleFadeIn {
  0% {
    opacity: 0;
    transform: translateY(20px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

/* 卡片悬停效果 - 硬朗男性化风格 */
.recent-post-item {
  border-radius: 12px;
  overflow: hidden;
  background: var(--bg-glass);
  backdrop-filter: blur(8px);
  border: 1px solid rgba(14, 165, 233, 0.2);
  box-shadow: 0 2px 8px rgba(30, 41, 59, 0.1);
  will-change: transform;
}

.recent-post-item:hover {
  transform: translateY(-4px);
  box-shadow: 0 8px 25px rgba(14, 165, 233, 0.2);
  border-color: var(--primary-color);
}

/* 头像 - 硬朗风格 */
.avatar-img {
  border-radius: 50%;
  border: 3px solid var(--primary-color);
  box-shadow: 0 0 15px rgba(14, 165, 233, 0.3);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.avatar-img:hover {
  transform: scale(1.08);
  box-shadow: 0 0 20px rgba(14, 165, 233, 0.5);
}

/* 导航栏 - 完全透明风格 */
#nav {
  background: transparent !important;
  backdrop-filter: none;
  border-bottom: none;
  box-shadow: none;
}

#nav .nav-link {
  color: #ffffff;
  font-weight: 600;
  font-size: 16px;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
  transition: all 0.3s ease;
  padding: 8px 16px;
  border-radius: 8px;
}

#nav .nav-link:hover {
  color: var(--primary-color);
  background: rgba(59, 130, 246, 0.1);
  text-shadow: 0 0 10px rgba(59, 130, 246, 0.8);
  transform: translateY(-1px);
}

/* 按钮 - 硬朗男性化风格 */
.btn, button {
  position: relative;
  overflow: hidden;
  background: var(--gradient-main);
  border: 2px solid var(--primary-color);
  border-radius: 8px;
  color: white;
  font-weight: 700;
  font-size: 14px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  padding: 12px 24px;
  box-shadow: 0 4px 12px rgba(14, 165, 233, 0.3);
}

.btn:hover, button:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 20px rgba(14, 165, 233, 0.4);
  border-color: var(--accent-color);
}

.btn::before, button::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(245, 158, 11, 0.4), transparent);
  transition: left 0.4s ease;
}

.btn:hover::before, button:hover::before {
  left: 100%;
